mapper.select:
SELECT
,jiaoshi.jiaoshi_name as jiaoshiName ,jiaoshi.jiaoshi_types as jiaoshiTypes ,jiaoshi.jiaoshi_photo as jiaoshiPhoto ,jiaoshi.jiaoshi_phone as jiaoshiPhone ,jiaoshi.jiaoshi_shichang as jiaoshiShichang ,jiaoshi.jiaoshi_content as jiaoshiContent ,jiaoshi.jiaoshi_delete as jiaoshiDelete
FROM zuoye a left JOIN jiaoshi jiaoshi ON a.jiaoshi_id = jiaoshi.id
and a.id in
#{item}
and ( a.jiaoshi_id = #{params.jiaoshiId} )
and a.zuoye_uuid_number like CONCAT('%',#{params.zuoyeUuidNumber},'%')
and a.zuoye_name like CONCAT('%',#{params.zuoyeName},'%')
and a.zuoye_types = #{params.zuoyeTypes}
and a.kemu_types = #{params.kemuTypes}
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.zuoye_content like CONCAT('%',#{params.zuoyeContent},'%')
and a.jiaoshi_id IS NOT NULL
and jiaoshi.jiaoshi_name like CONCAT('%',#{params.jiaoshiName},'%')
and jiaoshi.jiaoshi_types = #{params.jiaoshiTypes}
and jiaoshi.jiaoshi_phone like CONCAT('%',#{params.jiaoshiPhone},'%')
and jiaoshi.jiaoshi_shichang like CONCAT('%',#{params.jiaoshiShichang},'%')
and jiaoshi.jiaoshi_content like CONCAT('%',#{params.jiaoshiContent},'%')
and jiaoshi.jiaoshi_delete >= #{params.jiaoshiDeleteStart}
and jiaoshi.jiaoshi_delete <= #{params.jiaoshiDeleteEnd}
and jiaoshi.jiaoshi_delete = #{params.jiaoshiDelete}
and UNIX_TIMESTAMP(jiaoshi.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(jiaoshi.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
order by a.${params.sort} ${params.order}
